mapper.select:
select
short_url, long_url, user_id, id, created_at, updated_at, deleted_at, created_by, updated_by, deleted_by,
is_deleted
from link


        and short_url = #{entity.shortUrl}


        and long_url = #{entity.longUrl}


        and user_id = #{entity.userId}


        and id = #{entity.id}


        and created_at = #{entity.createdAt}


        and updated_at = #{entity.updatedAt}


        and deleted_at = #{entity.deletedAt}


        and created_by = #{entity.createdBy}


        and updated_by = #{entity.updatedBy}


        and deleted_by = #{entity.deletedBy}


        and is_deleted = #{entity.isDeleted}






            order by

                ${sort.property} ${sort.direction}



            order by id desc





`limit` #{pageable.offset}, #{pageable.pageSize}

select count(1)
from link


        and short_url = #{shortUrl}


        and long_url = #{longUrl}


        and user_id = #{userId}


        and id = #{id}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


        and deleted_at = #{deletedAt}


        and created_by = #{createdBy}


        and updated_by = #{updatedBy}


        and deleted_by = #{deletedBy}


        and is_deleted = #{isDeleted}
